import type { NotificationType } from './interfaces'

export function eventToType(eventCode: string | undefined): NotificationType {
  switch (eventCode) {
    case 'txSent':
    case 'txPool':
    case 'txSpeedUp':
    case 'txCancel':
      return 'pending'
    case 'txRequest':
    case 'txRepeat':
    case 'txAwaitingApproval':
    case 'txConfirmReminder':
    case 'txStallPending':
    case 'txStallConfirmed':
      return 'hint'
    case 'txError':
    case 'txSendFail':
    case 'txFailed':
    case 'txDropped':
    case 'nsfFail':
    case 'txUnderpriced':
      return 'error'
    case 'txConfirmed':
      return 'success'
    default:
      return 'hint'
  }
}

export function typeToDismissTimeout(type: string): number {
  switch (type) {
    case 'success':
    case 'hint':
      return 4000
    default:
      return 0
  }
}
import { _ } from 'svelte-i18n'
import BigNumber from 'bignumber.js'
import { notifications } from './stores'
import { eventToType, typeToDismissTimeout } from './defaults'
import { defaultNotifyMessages } from './i18n'

import type {
  CustomNotificationObject,
  TransactionData,
  NotificationType,
  BitcoinInputOutput
} from './interfaces'

// subscribe to the formatter store
let formatter: any
_.subscribe((store: any) => (formatter = store))

export function createNotification(
  transactionDetails: TransactionData,
  customization: CustomNotificationObject | boolean | void = {}
): void {
  const notificationObject =
    transactionDetails.system === 'bitcoin'
      ? createBitcoinNotificationObject(transactionDetails, customization)
      : createEthereumNotificationObject(transactionDetails, customization)

  notifications.add(notificationObject)
}

function createBitcoinNotificationObject(
  transactionDetails: TransactionData,
  customization: CustomNotificationObject | boolean | void
) {
  const {
    id,
    txid,
    startTime,
    eventCode,
    watchedAddress,
    inputs,
    outputs
  } = transactionDetails
  const type: NotificationType = eventToType(eventCode)
  const key = `${id}-${
    (typeof customization === 'object' && customization.eventCode) || eventCode
  }`

  const { direction, value } = getBitcoinDirectionValue(
    inputs,
    outputs,
    watchedAddress
  )

  const formatterOptions = watchedAddress
    ? {
        messageId: `watched['${eventCode}']`,
        values: {
          verb:
            eventCode === 'txConfirmed'
              ? direction === 'incoming'
                ? 'received'
                : 'sent'
              : direction === 'incoming'
              ? 'receiving'
              : 'sending',
          formattedValue: value,
          asset: 'BTC',
          preposition: '',
          counterpartyShortened: ''
        }
      }
    : {
        messageId: `transaction['${eventCode}']`,
        values: { value, asset: 'BTC' }
      }

  const internationalizedMessage = formatter(formatterOptions.messageId, {
    values: formatterOptions.values
  })

  const noMessageAvailable =
    internationalizedMessage === formatterOptions.messageId

  const message = noMessageAvailable
    ? defaultNotifyMessages.en[watchedAddress ? 'watched' : 'transaction'][
        eventCode || ''
      ]
    : internationalizedMessage

  let notificationObject = {
    id: id || txid,
    type,
    key,
    startTime,
    eventCode,
    message,
    autoDismiss: typeToDismissTimeout(
      (typeof customization === 'object' && customization.type) || type
    )
  }
  if (typeof customization === 'object') {
    notificationObject = { ...notificationObject, ...customization }
  }

  return notificationObject
}

function createEthereumNotificationObject(
  transactionDetails: TransactionData,
  customization: CustomNotificationObject | boolean | void
) {
  const {
    id,
    hash,
    startTime,
    eventCode,
    direction,
    counterparty,
    value,
    asset
  } = transactionDetails

  const type: NotificationType = eventToType(eventCode)
  const key = `${id}-${
    (typeof customization === 'object' && customization.eventCode) || eventCode
  }`
  const counterpartyShortened: string | undefined =
    counterparty &&
    counterparty.substring(0, 4) +
      '...' +
      counterparty.substring(counterparty.length - 4)

  const formattedValue = new BigNumber(value || 0)
    .div(new BigNumber('1000000000000000000'))
    .toString(10)

  const formatterOptions =
    counterparty && value
      ? {
          messageId: `watched['${eventCode}']`,
          values: {
            verb:
              eventCode === 'txConfirmed'
                ? direction === 'incoming'
                  ? 'received'
                  : 'sent'
                : direction === 'incoming'
                ? 'receiving'
                : 'sending',
            formattedValue,
            preposition: direction === 'incoming' ? 'from' : 'to',
            counterpartyShortened,
            asset
          }
        }
      : {
          messageId: `transaction['${eventCode}']`,
          values: { formattedValue, asset }
        }

  const internationalizedMessage = formatter(formatterOptions.messageId, {
    values: formatterOptions.values
  })

  const noMessageAvailable =
    internationalizedMessage === formatterOptions.messageId

  const message = noMessageAvailable
    ? defaultNotifyMessages.en[counterparty ? 'watched' : 'transaction'][
        eventCode || ''
      ]
    : internationalizedMessage

  let notificationObject = {
    id: id || hash,
    type,
    key,
    startTime,
    eventCode,
    message,
    autoDismiss: typeToDismissTimeout(
      (typeof customization === 'object' && customization.type) || type
    )
  }

  if (typeof customization === 'object') {
    notificationObject = { ...notificationObject, ...customization }
  }

  return notificationObject
}

function getBitcoinDirectionValue(
  inputs: BitcoinInputOutput[] | undefined,
  outputs: BitcoinInputOutput[] | undefined,
  watchedAddress: string | undefined
) {
  if (!watchedAddress) return { value: null, direction: '' }

  const finder = (i: BitcoinInputOutput) => i.address === watchedAddress

  const input = inputs && inputs.find(finder)
  const output = outputs && outputs.find(finder)

  return {
    direction: output ? 'incoming' : 'outgoing',
    value: output ? output.value : input && input.value
  }
}
import type {
  InitOptions,
  TransactionOptions,
  CustomNotificationObject,
  ConfigOptions
} from './interfaces'

const validInitKeys = [
  'dappId',
  'networkId',
  'system',
  'transactionHandler',
  'name',
  'onerror',
  'mobilePosition',
  'desktopPosition',
  'darkMode',
  'txApproveReminderTimeout',
  'txStallPendingTimeout',
  'txStallConfirmedTimeout',
  'notifyMessages',
  'clientLocale'
]

const validNotificationKeys = [
  'eventCode',
  'type',
  'message',
  'autoDismiss',
  'onclick'
]

const validTransactionKeys = [
  'sendTransaction',
  'estimateGas',
  'gasPrice',
  'balance',
  'contractCall',
  'txDetails'
]

function invalidParams(
  params: any,
  validParams: string[],
  functionName: string
): void | never {
  const invalid = Object.keys(params)

  if (invalid.length > 0) {
    throw new Error(
      `${
        invalid[0]
      } is not a valid parameter for ${functionName}, must be one of the following valid parameters: ${validParams.join(
        ', '
      )}`
    )
  }
}

export function validateType({
  name,
  value,
  type,
  optional,
  customValidation
}: {
  name: string
  value: any
  type: string
  optional?: boolean
  customValidation?: (val: any) => void | never
}): never | void {
  if (!optional && typeof value === 'undefined') {
    throw new Error(`"${name}" is required`)
  }

  if (
    typeof value !== 'undefined' &&
    (type === 'array' ? Array.isArray(type) : typeof value !== type)
  ) {
    throw new Error(
      `"${name}" must be of type: ${type}, received type: ${typeof value} from value: ${value}`
    )
  }

  if (typeof value !== 'undefined' && customValidation) {
    customValidation(value)
  }
}

export function validateInit(init: InitOptions): void {
  validateType({ name: 'init', value: init, type: 'object' })

  const {
    dappId,
    system,
    networkId,
    transactionHandler,
    name,
    apiUrl,
    onerror,
    ...otherParams
  } = init

  validateType({
    name: 'dappId',
    value: dappId,
    type: 'string',
    optional: true
  })

  validateType({
    name: 'system',
    value: system,
    type: 'string',
    // defaults to ethereum so optional
    optional: true
  })

  // if no dappId provided then optional, otherwise required
  validateType({
    name: 'networkId (if dappId provided)',
    value: networkId,
    type: 'number',
    optional: !dappId
  })

  validateType({ name: 'name', value: name, type: 'string', optional: true })

  validateType({
    name: 'apiUrl',
    value: apiUrl,
    type: 'string',
    optional: true
  })

  validateType({
    name: 'transactionHandler',
    value: transactionHandler,
    type: 'function',
    optional: true
  })

  validateType({
    name: 'onerror',
    value: onerror,
    type: 'function',
    optional: true
  })

  validateConfig(otherParams)
}

function stringOrNumber(val: string | number): boolean {
  return typeof val === 'string' || typeof val === 'number'
}

export function validateTransactionOptions(options: TransactionOptions): void {
  validateType({ name: 'transaction options', value: options, type: 'object' })

  const {
    sendTransaction,
    estimateGas,
    gasPrice,
    balance,
    contractCall,
    txDetails,
    ...otherParams
  } = options

  invalidParams(otherParams, validTransactionKeys, 'Transaction Options')

  validateType({
    name: 'sendTransaction',
    value: sendTransaction,
    type: 'function',
    optional: true
  })

  validateType({
    name: 'estimateGas',
    value: estimateGas,
    type: 'function',
    optional: true
  })

  validateType({
    name: 'gasPrice',
    value: gasPrice,
    type: 'function',
    optional: true
  })

  validateType({
    name: 'balance',
    value: balance,
    type: 'string',
    optional: true
  })

  validateType({
    name: 'contractCall',
    value: contractCall,
    type: 'object',
    optional: true
  })

  if (contractCall) {
    const { methodName, params, ...otherParams } = contractCall
    invalidParams(otherParams, ['methodName', 'params'], 'contractCall')

    validateType({
      name: 'methodName',
      value: methodName,
      type: 'string',
      optional: true
    })

    validateType({
      name: 'params',
      value: params,
      type: 'array',
      optional: true
    })
  }

  validateType({
    name: 'txDetails',
    value: txDetails,
    type: 'object',
    optional: true
  })

  if (txDetails) {
    const { to, value, from, ...otherParams } = txDetails

    invalidParams(otherParams, ['to', 'value', 'from'], 'txDetails')

    validateType({
      name: 'to',
      value: to,
      type: 'string',
      optional: true,
      customValidation: isAddress
    })

    if (typeof value !== 'undefined' && !stringOrNumber(value)) {
      throw new Error(
        `"value" must be of type: string | number, received type: ${typeof value} from value: ${value}`
      )
    }

    validateType({
      name: 'from',
      value: from,
      type: 'string',
      optional: true,
      customValidation: isAddress
    })
  }
}

export function validateNotificationObject(
  notification: CustomNotificationObject | boolean | undefined
): void {
  validateType({
    name: 'notification',
    value: notification,
    type: 'object'
  })

  if (typeof notification !== 'object') return

  const {
    eventCode,
    type,
    message,
    autoDismiss,
    onclick,
    ...otherParams
  } = notification

  invalidParams(otherParams, validNotificationKeys, 'notification')

  validateType({
    name: 'eventCode',
    value: eventCode,
    type: 'string',
    optional: true
  })

  validateType({
    name: 'type',
    value: type,
    type: 'string',
    optional: true,
    customValidation: validNotificationType
  })

  validateType({
    name: 'message',
    value: message,
    type: 'string'
  })

  validateType({
    name: 'autoDismiss',
    value: autoDismiss,
    type: 'number',
    optional: true
  })

  validateType({
    name: 'onclick',
    value: onclick,
    type: 'function',
    optional: true
  })
}

export function validateConfig(config: ConfigOptions): void {
  validateType({ name: 'config', value: config, type: 'object' })

  const {
    networkId,
    system,
    mobilePosition,
    desktopPosition,
    darkMode,
    notifyMessages,
    clientLocale,
    txApproveReminderTimeout,
    txStallPendingTimeout,
    txStallConfirmedTimeout,
    ...otherParams
  } = config

  invalidParams(otherParams, validInitKeys, 'config / initialize')

  validateType({
    name: 'networkId',
    value: networkId,
    type: 'number',
    optional: true
  })

  validateType({
    name: 'system',
    value: system,
    type: 'string',
    optional: true
  })

  validateType({
    name: 'mobilePosition',
    value: mobilePosition,
    type: 'string',
    optional: true,
    customValidation: validMobilePosition
  })

  validateType({
    name: 'desktopPosition',
    value: desktopPosition,
    type: 'string',
    optional: true,
    customValidation: validDesktopPosition
  })

  validateType({
    name: 'darkMode',
    value: darkMode,
    type: 'boolean',
    optional: true
  })

  validateType({
    name: 'notifyMessages',
    value: notifyMessages,
    type: 'object',
    optional: true
  })

  if (notifyMessages) {
    Object.keys(notifyMessages).forEach(locale => {
      validateType({
        name: locale,
        value: notifyMessages[locale],
        type: 'object'
      })

      const { transaction, watched, time, ...otherParams } = notifyMessages[
        locale
      ]

      invalidParams(otherParams, ['transaction', 'watched', 'time'], locale)

      validateType({
        name: `notifyMessages.${locale}.transaction`,
        value: transaction,
        type: 'object',
        optional: true
      })

      validateType({
        name: `notifyMessages.${locale}.watched`,
        value: watched,
        type: 'object',
        optional: true
      })

      validateType({
        name: `notifyMessages.${locale}.time`,
        value: time,
        type: 'object',
        optional: true
      })
    })
  }

  validateType({
    name: 'clientLocale',
    value: clientLocale,
    type: 'string',
    optional: true
  })

  validateType({
    name: 'txApproveReminderTimeout',
    value: txApproveReminderTimeout,
    type: 'number',
    optional: true
  })

  validateType({
    name: 'txStallPendingTimeout',
    value: txStallPendingTimeout,
    type: 'number',
    optional: true
  })

  validateType({
    name: 'txStallConfirmedTimeout',
    value: txStallConfirmedTimeout,
    type: 'number',
    optional: true
  })
}

function validNotificationType(type: string): void | never {
  switch (type) {
    case 'hint':
    case 'pending':
    case 'error':
    case 'success':
      return
    default:
      throw new Error(
        `${type} is not a valid notification type, must be one of: 'hint', 'pending', 'error' or 'success'.`
      )
  }
}

function validMobilePosition(position: string): void | never {
  switch (position) {
    case 'top':
    case 'bottom':
      return
    default:
      throw new Error(
        `${position} is not a valid mobile notification position, must be one of: 'top' or 'bottom'.`
      )
  }
}

function validDesktopPosition(position: string): void | never {
  switch (position) {
    case 'bottomLeft':
    case 'bottomRight':
    case 'topLeft':
    case 'topRight':
      return
    default:
      throw new Error(
        `${position} is not a valid desktop notification position, must be one of: 'bottomLeft', 'bottomRight', 'topLeft' or 'topRight'.`
      )
  }
}

function isAddress(address: string): void | never {
  if (!/^(0x)?[0-9a-fA-F]{40}$/.test(address)) {
    throw new Error(`${address} is not a valid ethereum address.`)
  }
}
import BigNumber from 'bignumber.js'
import uuid from 'uuid/v4'
import { get } from 'svelte/store'

import { transactions, app } from './stores'
import { createNotification } from './notifications'
import { argsEqual, extractMessageFromError, localNetwork } from './utilities'
import { validateNotificationObject } from './validation'

import type {
  TransactionData,
  PreflightEvent,
  ContractCall,
  CustomNotificationObject,
  Emitter,
  TransactionOptions
} from './interfaces'

let transactionQueue: TransactionData[]
transactions.subscribe((store: TransactionData[]) => (transactionQueue = store))

export function handlePreFlightEvent(
  blocknative,
  preflightEvent: PreflightEvent
) {
  const {
    eventCode,
    contractCall,
    balance,
    txDetails,
    emitter,
    status
  } = preflightEvent

  let contract

  if (contractCall) {
    contract = {
      methodName: contractCall.methodName,
      parameters: contractCall.params
    }
  }

  blocknative.event({
    categoryCode: contractCall ? 'activeContract' : 'activeTransaction',
    eventCode,
    transaction: txDetails,
    wallet: { balance },
    contract: contractCall ? contract : undefined
  })

  const transaction = {
    ...txDetails,
    eventCode,
    status,
    contractCall: contract ? contractCall : undefined
  }

  const emitterResult = emitter.emit(transaction)

  if (emitterResult) {
    validateNotificationObject(emitterResult)
  }

  handleTransactionEvent({
    transaction: transaction,
    emitterResult
  })
}

export function handleTransactionEvent(event: {
  transaction: TransactionData
  emitterResult: boolean | void | CustomNotificationObject
}) {
  const { transaction, emitterResult } = event
  transactions.updateQueue(transaction)

  // create notification if dev hasn't opted out and not connected to a local network
  if (emitterResult !== false && !localNetwork(get(app).networkId)) {
    const transactionObj = transactionQueue.find(
      (tx: TransactionData) => tx.id === transaction.id
    )
    if (transactionObj) {
      createNotification(transactionObj, emitterResult)
    }
  }
}

export function duplicateTransactionCandidate(
  transaction: TransactionData,
  contract: ContractCall
) {
  const duplicate:
    | TransactionData
    | undefined
    | boolean = transactionQueue.find((tx: TransactionData) => {
    if (contract && typeof tx.contractCall === 'undefined') return false
    if (tx.status === 'confirmed' || tx.status === 'failed') return

    const sameMethod = contract
      ? contract.methodName === (tx.contractCall && tx.contractCall.methodName)
      : true

    const sameParams = contract
      ? argsEqual(contract.params, tx.contractCall && tx.contractCall.params)
      : true

    const sameVal = tx.value == transaction.value

    const sameTo = contract
      ? sameMethod
      : tx.to &&
        tx.to.toLowerCase() === transaction.to &&
        transaction.to.toLowerCase()

    return sameMethod && sameParams && sameVal && sameTo
  })

  return duplicate
}

export function preflightTransaction(
  blocknative,
  options: TransactionOptions,
  emitter: Emitter
): Promise<string> {
  return new Promise((resolve, reject) => {
    // wrap in set timeout to put to the end of the event queue
    setTimeout(async () => {
      const {
        sendTransaction,
        estimateGas,
        gasPrice,
        balance,
        contractCall,
        txDetails
      } = options

      //=== if `balance` or `estimateGas` or `gasPrice` is not provided, then sufficient funds check is disabled === //
      //=== if `txDetails` is not provided, then duplicate transaction check is disabled === //
      //== if dev doesn't want notify to intiate the transaction and `sendTransaction` is not provided, then transaction rejected notification is disabled ==//
      //=== to disable hints for `txAwaitingApproval`, `txConfirmReminder` or any other notification, then return false from listener functions ==//

      const [gas, price] = await gasEstimates(estimateGas, gasPrice)
      const id = uuid()
      const value = new BigNumber((txDetails && txDetails.value) || 0)

      const calculated = {
        value: value.toString(10),
        gas: gas && gas.toString(10),
        gasPrice: price && price.toString(10)
      }

      const txObject = txDetails
        ? {
            ...txDetails,
            ...calculated,
            id
          }
        : { ...calculated, id }

      // check sufficient balance if required parameters are available
      if (balance && gas && price) {
        const transactionCost = gas.times(price).plus(value)

        // if transaction cost is greater than the current balance
        if (transactionCost.gt(new BigNumber(balance))) {
          const eventCode = 'nsfFail'

          handlePreFlightEvent(blocknative, {
            eventCode,
            contractCall,
            balance,
            txDetails: txObject,
            emitter
          })

          return reject('User has insufficient funds')
        }
      }

      // check if it is a duplicate transaction
      if (txDetails && duplicateTransactionCandidate(txDetails, contractCall)) {
        const eventCode = 'txRepeat'

        handlePreFlightEvent(blocknative, {
          eventCode,
          contractCall,
          balance,
          txDetails: txObject,
          emitter
        })
      }

      const {
        txApproveReminderTimeout,
        txStallPendingTimeout,
        txStallConfirmedTimeout
      } = get(app)

      // check previous transactions awaiting approval
      if (transactionQueue.find(tx => tx.status === 'awaitingApproval')) {
        const eventCode = 'txAwaitingApproval'

        handlePreFlightEvent(blocknative, {
          eventCode,
          contractCall,
          balance,
          txDetails: txObject,
          emitter
        })
      }

      // confirm reminder after timeout
      setTimeout(() => {
        const awaitingApproval = transactionQueue.find(
          tx => tx.id === id && tx.status === 'awaitingApproval'
        )

        if (awaitingApproval) {
          const eventCode = 'txConfirmReminder'

          handlePreFlightEvent(blocknative, {
            eventCode,
            contractCall,
            balance,
            txDetails: txObject,
            emitter
          })
        }
      }, txApproveReminderTimeout)

      handlePreFlightEvent(blocknative, {
        eventCode: 'txRequest',
        status: 'awaitingApproval',
        contractCall,
        balance,
        txDetails: txObject,
        emitter
      })

      // if not provided with sendTransaction function, resolve with id so dev can initiate transaction
      // dev will need to call notify.hash(txHash, id) with this id to link up the preflight with the postflight notifications
      if (!sendTransaction) {
        return resolve(id)
      }

      // get result and handle errors
      let hash
      try {
        hash = await sendTransaction()
      } catch (error) {
        const { eventCode, errorMsg } = extractMessageFromError(error)

        handlePreFlightEvent(blocknative, {
          eventCode,
          status: 'failed',
          contractCall,
          balance,
          txDetails: txObject,
          emitter
        })

        return reject(errorMsg)
      }

      if (hash && typeof hash === 'string') {
        const serverEmitter = blocknative.transaction(hash, id).emitter

        serverEmitter.on('all', (transaction: TransactionData) => {
          const result = emitter.emit(transaction)
          return result
        })

        // Check for pending stall status
        setTimeout(() => {
          const transaction = transactionQueue.find(
            (tx: TransactionData) => tx.id === id
          )
          if (
            transaction &&
            transaction.status === 'sent' &&
            blocknative._connected
          ) {
            const eventCode = 'txStallPending'

            handlePreFlightEvent(blocknative, {
              eventCode,
              contractCall,
              balance,
              txDetails: txObject,
              emitter
            })
          }
        }, txStallPendingTimeout)

        // Check for confirmed stall status
        setTimeout(() => {
          const transaction = transactionQueue.find(tx => tx.id === id)

          if (
            transaction &&
            transaction.status === 'pending' &&
            blocknative._connected
          ) {
            const eventCode = 'txStallConfirmed'

            handlePreFlightEvent(blocknative, {
              eventCode,
              contractCall,
              balance,
              txDetails: txObject,
              emitter
            })
          }
        }, txStallConfirmedTimeout)

        resolve(id)
      } else {
        reject(
          'sendTransaction function must resolve to a transaction hash that is of type String.'
        )
      }
    }, 10)
  })
}

function gasEstimates(
  gasFunc: () => Promise<string>,
  gasPriceFunc: () => Promise<string>
) {
  if (!gasFunc || !gasPriceFunc) {
    return Promise.resolve([])
  }

  const gasProm = gasFunc()
  if (!gasProm.then) {
    throw new Error('The `estimateGas` function must return a Promise')
  }

  const gasPriceProm = gasPriceFunc()
  if (!gasPriceProm.then) {
    throw new Error('The `gasPrice` function must return a Promise')
  }

  return Promise.all([gasProm, gasPriceProm])
    .then(([gasResult, gasPriceResult]) => {
      if (typeof gasResult !== 'string') {
        throw new Error(
          `The Promise returned from calling 'estimateGas' must resolve with a value of type 'string'. Received a value of: ${gasResult} with a type: ${typeof gasResult}`
        )
      }

      if (typeof gasPriceResult !== 'string') {
        throw new Error(
          `The Promise returned from calling 'gasPrice' must resolve with a value of type 'string'. Received a value of: ${gasPriceResult} with a type: ${typeof gasPriceResult}`
        )
      }

      return [new BigNumber(gasResult), new BigNumber(gasPriceResult)]
    })
    .catch(error => {
      throw new Error(`There was an error getting gas estimates: ${error}`)
    })
}
import 'regenerator-runtime/runtime'
import BlocknativeSdk from 'bnc-sdk'
import { get } from 'svelte/store'

import uuid from 'uuid/v4'
import { locale, dictionary, getClientLocale } from 'svelte-i18n'

import Notify from './views/Notify.svelte'

import { app, notifications } from './stores'
import { handleTransactionEvent, preflightTransaction } from './transactions'
import { createNotification } from './notifications'

import type {
  InitOptions,
  TransactionHandler,
  AppStore,
  API,
  Emitter,
  TransactionOptions,
  CustomNotificationObject,
  UpdateNotification,
  ConfigOptions,
  LocaleMessages
} from './interfaces'

export {
  InitOptions,
  TransactionHandler,
  TransactionEvent,
  System,
  TransactionEventCode,
  TransactionData,
  NotificationType,
  CustomNotificationObject,
  BitcoinInputOutput,
  NotificationObject,
  ContractCall,
  DecodedContractCall,
  NotifyMessages,
  LocaleMessages,
  TransactionOptions,
  PreflightEvent,
  UpdateNotification,
  ConfigOptions,
  Hash,
  Transaction,
  Account,
  Unsubscribe,
  Notification,
  Config,
  API,
  EmitterListener,
  Emitter,
  NotificationDetails
} from './interfaces'

import {
  validateInit,
  validateTransactionOptions,
  validateNotificationObject,
  validateConfig
} from './validation'

import { createEmitter } from './utilities'

import { version } from '../package.json'

let notify: any
let blocknative

function init(options: InitOptions): API {
  if (notify) {
    console.warn('notify has already been initialized')
    notify.$destroy()
    blocknative && blocknative.destroy()
  }

  validateInit(options)

  const { system, transactionHandler, apiUrl, ...appOptions } = options
  const { dappId, networkId, name, clientLocale, onerror } = appOptions

  const transactionHandlers: TransactionHandler[] = [handleTransactionEvent]

  if (transactionHandler) {
    transactionHandlers.push(transactionHandler)
  }

  if (dappId) {
    blocknative = new BlocknativeSdk({
      dappId,
      networkId,
      onerror,
      transactionHandlers,
      name: name || 'Notify',
      apiUrl,
      system
    })

    // filter out pending simulation events
    blocknative
      .configuration({
        scope: 'global',
        filters: [{ status: 'pending-simulation', _not: true }]
      })
      .catch(() => {
        // swallow server timeout response error as we are not waiting on it
      })
  }

  // save config to app store
  app.update((store: AppStore) => ({
    ...store,
    ...appOptions,
    version,
    clientLocale:
      clientLocale ||
      getClientLocale({
        fallback: 'en',
        navigator: true
      })
  }))

  // initialize App
  notify = new Notify({
    target: document.body
  })

  app.subscribe((store: AppStore) => {
    const { notifyMessages, clientLocale } = store

    // set the dictionary for i18n
    dictionary.set(notifyMessages)

    const availableLocale: LocaleMessages | undefined =
      notifyMessages[clientLocale] || notifyMessages[clientLocale.slice(0, 2)]
    locale.set(availableLocale ? clientLocale : 'en')
  })

  return {
    hash,
    transaction,
    account,
    unsubscribe,
    notification,
    config
  }

  function account(
    address: string
  ): { details: { address: string }; emitter: Emitter } | never {
    if (!blocknative) {
      throw new Error(
        'A dappId needs to be passed in when intializing Notify to use the account function'
      )
    }

    const result = blocknative.account(address)
    return result
  }

  function hash(hash: string, id?: string) {
    if (!blocknative) {
      throw new Error(
        'A dappId needs to be passed in when intializing Notify to use the hash function'
      )
    }

    const result = blocknative.transaction(hash, id)
    return result
  }

  function transaction(
    options: TransactionOptions
  ): { result: Promise<string>; emitter: Emitter } {
    if (!blocknative) {
      throw new Error(
        'A dappId needs to be passed in when intializing Notify to use the transaction function'
      )
    }

    validateTransactionOptions(options)

    const emitter = createEmitter()

    const result = preflightTransaction(blocknative, options, emitter).catch(
      err => {
        const { onerror } = get(app)
        onerror && onerror(err)
        return err
      }
    )

    return {
      emitter,
      result
    }
  }

  function unsubscribe(addressOrHash: string) {
    if (!blocknative) {
      throw new Error(
        'A dappId needs to be passed in when intializing Notify to use the unsubscribe function'
      )
    }

    blocknative.unsubscribe(addressOrHash)
  }

  function notification(
    notificationObject: CustomNotificationObject
  ): {
    dismiss: () => void
    update: UpdateNotification
  } {
    validateNotificationObject(notificationObject)

    let key = 0

    const id: string = uuid()
    const startTime: number = Date.now()
    const { eventCode = `customNotification${key++}` } = notificationObject

    const dismiss = () => notifications.remove(id, eventCode)

    function update(
      notificationUpdate: CustomNotificationObject
    ): {
      dismiss: () => void
      update: UpdateNotification
    } {
      validateNotificationObject(notificationUpdate)

      const { eventCode = `customNotification${key++}` } = notificationUpdate
      createNotification({ id, startTime, eventCode }, notificationUpdate)

      return {
        dismiss,
        update
      }
    }

    createNotification({ id, startTime, eventCode }, notificationObject)

    return {
      dismiss,
      update
    }
  }

  function config(options: ConfigOptions): void {
    validateConfig(options)

    const {
      notifyMessages,
      networkId: newNetworkId,
      system: newSystem,
      ...otherOptions
    } = options

    const { networkId, system, dappId, transactionHandler, name, apiUrl } = get(
      app
    )

    // networkId or system has changed
    if (
      (newNetworkId && newNetworkId !== networkId) ||
      (newSystem && newSystem !== system)
    ) {
      if (!blocknative) {
        throw new Error(
          'A dappId needs to be passed in when intializing Notify to be able to connect to a system and network'
        )
      }

      // close existing SDK connection
      blocknative.destroy()

      // create new connection with new values
      blocknative = new BlocknativeSdk({
        dappId,
        networkId: newNetworkId || networkId,
        transactionHandlers: transactionHandler
          ? [handleTransactionEvent, transactionHandler]
          : [handleTransactionEvent],
        name: name || 'Notify',
        apiUrl,
        system: newSystem || system
      })
    }

    app.update((store: AppStore) => {
      return {
        ...store,
        networkId: newNetworkId || networkId,
        system: newSystem || system,
        ...otherOptions,
        notifyMessages: notifyMessages
          ? { ...store.notifyMessages, ...notifyMessages }
          : store.notifyMessages
      }
    })
  }
}

export default init
